import 'package:flutter/material.dart';

class CardLivro extends StatelessWidget {
  final VoidCallbackAction onTap;
  
  final String imgSrc = "data:image/jpeg;base64,";
  const CardLivro({
    Key? key,
    required this.onTap,
  }) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Container(
      child: Column(
        children: [Image.network(imgSrc)],
      ),
    );
  }
}
